<?php$timezone_string="Asia/Ho_Chi_Minh";date_default_timezone_set($timezone_string);$jsonWebhookData = file_get_contents("php://input");$webhookData = json_decode($jsonWebhookData, true);//Get và remove trường sign ra khỏi dữ liệu$baokimSign = $webhookData['sign'];unset($webhookData['sign']);//Chuyển dữ liệu đã remove sign về lại dạng json và sử dụng thuật toán hash sha256 để tạo signature với secret key$signData = json_encode($webhookData);$secret = "9623ac03057e433f95d86cf4f3bef5cc";$mySign = hash_hmac('sha256', $signData, $secret);//So sánh chữ ký bạn tạo ra với chữ ký bảo kim gửi sang, nếu khớp thì verify thành côngif($baokimSign == $mySign) {    echo "Signature is valid";	$myFile =  'log.txt';	$fh = fopen($myFile, 'a') or die("can't open file");	fwrite($fh, date('Y-m-d H:i:s').' valid '.$baokimSign."\n"); 	} else {    echo "Signature is invalid";	$myFile =  'log.txt';	$fh = fopen($myFile, 'a') or die("can't open file");	fwrite($fh, date('Y-m-d H:i:s').' invalid '.$baokimSign."\n"); 	}?>